import React, { useEffect, useState } from 'react'
import { NavLink} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import '../about.css'


const Header = () => {
    const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
    return (
        <div>
            <div className='container-fluid navColor rowPad'>
                <div className='row'>
                    <div className='col-md-12 '>
                        {/* <div className='container-fluid navSticky'>
                            <div className='row'> */}
                                <nav className={`navColor ${scrolled ? 'scrolled' : ''}`}>
                                    <navbar className="navbar navbar-expand-lg bg-body-tertiar navSection">
                                        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                                            <span className="navbar-toggler-icon"></span>
                                        </button>
                                            <div className="collapse navbar-collapse justify-content-end" id="navbarNavDropdown">
                                                <ul className="navbar-nav m-0 navItemColor">
                                                    <li className="nav-item">
                                                        <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} aria-current="page" to="/">Home</NavLink>
                                                    </li>
                                                    <li className="nav-item">
                                                        <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} to="/aboutme">About Me</NavLink>
                                                    </li>
                                                    <li className="nav-item">
                                                        <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} to="/projects">Projects</NavLink>
                                                    </li>
                                                </ul>
                                            </div>
                                    </navbar>
                                </nav>
                            </div>
                        {/* </div>
                    </div> */}
                </div>
            </div>
        </div>
    )
}

export default Header
