import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import '../about.css'


const Header = () => {
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    return (
        <div>
            <div className='bgImg'>
            <div className='container-fluid navColor'>
        <div className='row'>
        <nav className={`navColor ${scrolled ? 'scrolled' : ''}`}>
            <navbar className="navbar navbar-expand-lg bg-body-tertiar">
                {scrolled && <div className='col-md-3 prodigyLogo'>
                   <img src={logowhite} alt='' />
                </div>}
                {!scrolled && <div className='col-md-3 prodigyLogo'>
                   <img src={logo2} alt='' />
                </div>}
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                  <span className="navbar-toggler-icon"></span>
                </button>
                <div className='col-md-8 navMenu-padding'>
                <div className="collapse navbar-collapse justify-content-end navSection" id="navbarNavDropdown">
                  <ul className="navbar-nav m-0 navItemColor">
                    <li className="nav-item">
                      <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} aria-current="page" to="/">Home</NavLink>
                    </li>
                    <li className="nav-item">
                      <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} to="/aboutme">About Me</NavLink>
                    </li>
                    <li className="nav-item">
                      <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} to="/stopwatch">Stopwatch</NavLink>
                    </li>  
                  </ul>
                </div>
              </div>
            </navbar>
            </nav>
          </div>
          </div>
          </div>
            <div>
                <h1>
                    Education
                </h1>
                <p>Our mission is to create innovative and accessible learning solutions that empower people of all ages and backgrounds to achieve their full potential. Whether you're a student looking to improve your grades, a professional seeking to upskill, or an organization looking to enhance employee training, we have the tools and resources you need to succeed.

If you're looking for an innovative and dynamic learning experience that will help you achieve your goals and unlock your full potential, we invite you to join us on this exciting journey. Together, we can transform the way we learn and create a better future for all.</p>
            </div>
        </div>
    )
}

export default Header
