import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';
import '../about.css'


const Header = () => {
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true);
            } else {
                setScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    return (
        <div>
            <div className='bgImg'>
            <div className='container-fluid navColor'>
                <div className='row'>
                <nav className={`navColor ${scrolled ? 'scrolled' : ''}`}>
                <navbar className="navbar navbar-expand-lg bg-body-tertiar">
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavDropdown" aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
                  <span className="navbar-toggler-icon"></span>
                </button>
                <div className='col-md-12 navMenu-padding'>
                <div className="collapse navbar-collapse justify-content-end navSection" id="navbarNavDropdown">
                  <ul className="navbar-nav m-0 navItemColor">
                    <li className="nav-item">
                      <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} aria-current="page" to="/">Home</NavLink>
                    </li>
                    <li className="nav-item">
                      <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} to="/aboutme">About Me</NavLink>
                    </li>
                    <li className="nav-item">
                      <NavLink className={`nav-link ${scrolled ? 'scrolled' : ''}`} to="/stopwatch">Stopwatch</NavLink>
                    </li>  
                  </ul>
                </div>
              </div>
            </navbar>
            </nav>
          </div>
          </div>
          <div className='col-md-10'>
             <div className='ui-dev'>
                <h1>UI DEVELOPER</h1>
             </div>
          </div>
          </div>
           <div className='row my-5'>
              <div className='col-md-12'>
                <h3>Technical Skills</h3>
                <ul>
                    <li>Web Technologies  :   HTML5, CSS3, JSON</li>
                    <li>Programming Languages  :   JavaScript</li>
                    <li>Frameworks  :   React JS, Bootstrap</li>
                    <li>IDEâ€™s/Text Editors  :   Visual Studio Code, Notepad++</li>
                </ul>
              </div>
           </div>
        </div>
    )
}

export default Header
